from pybricks.hubs import InventorHub
from pybricks.parameters import Color, Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
inventor_hub = InventorHub()
right_color_sensor = ColorSensor(Port.E)
left_color_sensor = ColorSensor(Port.F)
left_motor = Motor(Port.D, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.C, Direction.CLOCKWISE)
drive_base = DriveBase(left_motor, right_motor, 56, 102)
right_attachment_motor = Motor(Port.A, Direction.CLOCKWISE)
motor_2 = Motor(Port.B, Direction.CLOCKWISE)

# Initialize variables.
gain_lf = 0.1
target_light = 50
power_lf = 20
correction_lf = 0

def line_follow_right_right():
    global correction_lf
    correction_lf = (right_color_sensor.reflection() - target_light) * gain_lf
    left_motor.dc(power_lf + correction_lf)
    right_motor.dc(power_lf - correction_lf)


# The main program starts here.
drive_base.use_gyro(True)
drive_base.settings(straight_speed=200)
drive_base.settings(turn_rate=100)
inventor_hub.imu.reset_heading(0)
while not right_color_sensor.reflection() < 30:
    drive_base.drive(300, 0)
while not left_color_sensor.color() == Color.BLUE:
    line_follow_right_right()
while not abs(inventor_hub.imu.heading()) > 105:
    right_motor.run(400)
drive_base.straight(0)
print(inventor_hub.imu.heading())
while True:
    wait(1000)
